import React, { useEffect } from 'react';
import { useState } from 'react';

import './App.css';

import { MenuItem,FormControl,Select, Card } from '@material-ui/core';
import Infobox from './Infobox';
import Map from './Map.js';




function App() {
  const [country,setCountry] = useState("worldwide")
  const [countries,setCountries] = useState([])


  useEffect(() => {
    const getCountriesData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
      .then((response) =>response.json())
      .then((data) => {
        const countries =data.map((country) =>({
          name:country.country,
          value:country.countryInfo.iso2
        }));
        setCountries(countries);
      });
    };
    getCountriesData();
  },[]);


  const onCountryChange =(event) =>
  {
    const countryCode = event.target.value;
    setCountry(countryCode);
  };

  return (
    <div className="app">
      <div className='app_left'>
      <div className='app_header'>
      <h1> COVID-19 TRACKER</h1>
      <FormControl className='app_dropdown'>
<Select variant="outlined" value={country}  onChange={onCountryChange}  >

<MenuItem value="worldwide">worldwide</MenuItem>

  {countries.map((country)=>(
    <MenuItem value={country.value}>{country.name}</MenuItem>

  ))}

</Select>
      </FormControl>

      </div>

     <div className='app_stats'>
    <Infobox title="coronavirus cases" cases={100} total={3000} /> 
    <Infobox title="recovered"cases={900} total={4000}/> 
    <Infobox title="deaths" cases={160} total={7000} /> 
    </div>
   <Map />
    </div>

<Card className='app_right'>
<h1>live cases by contry</h1>
<h1>worldwide cases</h1>
</Card>

    </div>
  );
}

export default App;
